mapper.update:

UPDATE  GOODS 
SET     GOODS_THUMBNAIL = (
                    SELECT  GOODS_IMAGE_STD
                    FROM    (
                            SELECT  GOODS_IMAGE_STD 
                            FROM    GOODS_IMAGE 
                            WHERE   GOODS_IMAGE_PARENT = #{IDX} 

                            ORDER BY GOODS_IMAGE_NUM ASC
                            )
                    WHERE   ROWNUM <= 1
                    )
WHERE   GOODS_NUM = #{IDX}



UPDATE 	GOODS_IMAGE 
SET 	GOODS_IMAGE_DEL_GB = 'Y' 
WHERE 	GOODS_IMAGE_PARENT = #{IDX}



UPDATE 	GOODS_IMAGE 
SET 	GOODS_IMAGE_DEL_GB = 'N' 
WHERE 	GOODS_IMAGE_NUM = #{FILE_IDX}
